var carousel;
var _w = $(window).width();
$(document).ready(function(){
	$('body').imagesStatus({
		allImgFinished:function(container){
			if(_w<=576){
				carousel = M.Carousel.init($('.carousel'),{shift:80,});
				$('.lead-content').html($('.lead-content').html().replace(/<br>|<br\/>/ig,' '));
				console.log($('.lead-content').html());
			}else if(_w>576 && _w<=768){
				carousel = M.Carousel.init($('.carousel'),{shift:150});
			}else if(_w>768 && _w<=1024){
				carousel = M.Carousel.init($('.carousel'),{shift:220});
			}else{
				carousel = M.Carousel.init($('.carousel'),{shift:280});
			}
		}
	});
	$('.section1 .btn-prev').on('click',function(){carousel[0].prev()});
	$('.section1 .btn-next').on('click',function(){carousel[0].next()});
	$('.section3 .btn-prev').on('click',function(){carousel[1].prev()});
	$('.section3 .btn-next').on('click',function(){carousel[1].next()});
	new WOW().init();
})

function isMobile(){
	return (/Android|iPhone|iPad|iPod|BlackBerry/i).test(navigator.userAgent || navigator.vendor || window.opera);
}